detection.query:
selection_reg_img: [{"Image|endswith": "\\reg.exe"}, {"OriginalFileName": "reg.exe"}]
selection_reg_cli: {"CommandLine|contains|all": ["add", "CurrentVersion\\Control Panel\\CPLs"]}
selection_cpl: {"CommandLine|endswith": ".cpl"}
filter_cpl_sys: {"CommandLine|contains": ["\\System32\\", "%System%", "|C:\\Windows\\system32|"]}
filter_cpl_igfx: {"CommandLine|contains|all": ["regsvr32 ", " /s ", "igfxCPL.cpl"]}
