detection.query:
selection_word_paths: [{"TargetFilename|contains": "\\Microsoft\\Word\\STARTUP"}, {"TargetFilename|contains|all": ["\\Office", "\\Program Files", "\\STARTUP"]}]
selection_word_extension: {"TargetFilename|endswith": [".doc", ".docm", ".docx", ".dot", ".dotm", ".rtf"]}
selection_excel_paths: [{"TargetFilename|contains": "\\Microsoft\\Excel\\XLSTART"}, {"TargetFilename|contains|all": ["\\Office", "\\Program Files", "\\XLSTART"]}]
selection_excel_extension: {"TargetFilename|endswith": [".xls", ".xlsm", ".xlsx", ".xlt", ".xltm"]}
filter_main_office: {"Image|endswith": ["\\WINWORD.exe", "\\EXCEL.exe"]}
