detection.query:
selection_reg: {"Image|endswith": "\\reg.exe", "CommandLine|contains": ["HKEY_CURRENT_USER\\Control Panel\\Desktop", "HKCU\\Control Panel\\Desktop"]}
selection_option_1: {"CommandLine|contains|all": ["/v ScreenSaveActive", "/t REG_SZ", "/d 1", "/f"]}
selection_option_2: {"CommandLine|contains|all": ["/v ScreenSaveTimeout", "/t REG_SZ", "/d ", "/f"]}
selection_option_3: {"CommandLine|contains|all": ["/v ScreenSaverIsSecure", "/t REG_SZ", "/d 0", "/f"]}
selection_option_4: {"CommandLine|contains|all": ["/v SCRNSAVE.EXE", "/t REG_SZ", "/d ", ".scr", "/f"]}
