detection.query:
selection: [{"Image|endswith": [":\\Windows\\System32\\bash.exe", ":\\Windows\\SysWOW64\\bash.exe"]}, {"OriginalFileName": "Bash.exe"}]
filter_main_cli_flag: {"CommandLine|contains": ["bash.exe -", "bash -"]}
filter_main_no_cli: {"CommandLine": null}
filter_main_empty: {"CommandLine": ""}
filter_main_no_flag: {"CommandLine": ["bash.exe", "bash"]}
