detection.query:
selection: {"TargetFilename|endswith": ".lnk", "TargetFilename|contains": [".doc.", ".docx.", ".jpg.", ".pdf.", ".ppt.", ".pptx.", ".xls.", ".xlsx."]}
filter_main_recent: {"TargetFilename|contains": "\\AppData\\Roaming\\Microsoft\\Windows\\Recent\\"}
filter_optional_office_recent: {"Image|endswith": ["\\excel.exe", "\\powerpnt.exe", "\\winword.exe"], "TargetFilename|contains": "\\AppData\\Roaming\\Microsoft\\Office\\Recent\\"}
filter_optional_office_excel: {"Image|endswith": "\\excel.exe", "TargetFilename|contains": "\\AppData\\Roaming\\Microsoft\\Excel"}
filter_optional_office_powerpoint: {"Image|endswith": "\\powerpnt.exe", "TargetFilename|contains": "\\AppData\\Roaming\\Microsoft\\PowerPoint"}
filter_optional_office_word: {"Image|endswith": "\\winword.exe", "TargetFilename|contains": "\\AppData\\Roaming\\Microsoft\\Word"}
