detection.query:
selection_other_img: {"Image|endswith": ["\\whoami.exe", "\\quser.exe", "\\qwinsta.exe"]}
selection_other_wmi: {"Image|endswith": "\\wmic.exe", "CommandLine|contains|all": ["useraccount", "get"]}
selection_other_cmdkey: {"Image|endswith": "\\cmdkey.exe", "CommandLine|contains": " /l"}
selection_cmd: {"Image|endswith": "\\cmd.exe", "CommandLine|contains|all": [" /c", "dir ", "\\Users\\"]}
filter_cmd: {"CommandLine|contains": " rmdir "}
selection_net: {"Image|endswith": ["\\net.exe", "\\net1.exe"], "CommandLine|contains": "user"}
filter_net: {"CommandLine|contains": ["/domain", "/add", "/delete", "/active", "/expires", "/passwordreq", "/scriptpath", "/times", "/workstations"]}
