detection.query:
selection_tool_apt: {"Image|endswith": ["/apt", "/apt-get"], "CommandLine|contains": "install"}
selection_tool_yum: {"Image|endswith": "/yum", "CommandLine|contains": ["localinstall", "install"]}
selection_tool_rpm: {"Image|endswith": "/rpm", "CommandLine|contains": "-i"}
selection_tool_dpkg: {"Image|endswith": "/dpkg", "CommandLine|contains": ["--install", "-i"]}
selection_keyword: {"CommandLine|contains": ["nmap", " nc", "netcat", "wireshark", "tshark", "openconnect", "proxychains"]}
