detection.query:
selection_iwr: {"Image|endswith": ["\\powershell.exe", "\\pwsh.exe", "\\cmd.exe"], "CommandLine|contains": ["Invoke-WebRequest", "iwr ", "wget ", "curl "], "CommandLine|contains|all": [" -ur", " -me", " -b", " POST "]}
selection_curl: {"Image|endswith": "\\curl.exe", "CommandLine|contains": "--ur"}
selection_curl_data: {"CommandLine|contains": [" -d ", " --data "]}
selection_wget: {"Image|endswith": "\\wget.exe", "CommandLine|contains": ["--post-data", "--post-file"]}
payloads: [{"CommandLine|contains": ["Get-Content", "GetBytes", "hostname", "ifconfig", "ipconfig", "net view", "netstat", "nltest", "qprocess", "sc query", "systeminfo", "tasklist", "ToBase64String", "whoami"]}, {"CommandLine|contains|all": ["type ", " > ", " C:\\"]}]
