detection.query:
selection_target: {"CommandLine|contains": ["\\\\\\\\*$", "\\Sysvol\\"]}
selection_other_tools: [{"Image|endswith": ["\\robocopy.exe", "\\xcopy.exe"]}, {"OriginalFileName": ["robocopy.exe", "XCOPY.EXE"]}]
selection_cmd_img: [{"Image|endswith": "\\cmd.exe"}, {"OriginalFileName": "Cmd.Exe"}]
selection_cmd_cli: {"CommandLine|contains": "copy"}
selection_pwsh_img: [{"Image|contains": ["\\powershell.exe", "\\pwsh.exe"]}, {"OriginalFileName": ["PowerShell.EXE", "pwsh.dll"]}]
selection_pwsh_cli: {"CommandLine|contains": ["copy-item", "copy ", "cpi ", " cp ", "move ", "move-item", " mi ", " mv "]}
