detection.query:
selection_img: [{"OriginalFileName": "python.exe"}, {"Image|endswith": ["python.exe", "python3.exe", "python2.exe"]}]
selection_cli: {"CommandLine|contains": " -c"}
filter_main_python: {"ParentImage|startswith": "C:\\Program Files\\Python", "ParentImage|endswith": "\\python.exe", "ParentCommandLine|contains": "-E -s -m ensurepip -U --default-pip"}
filter_optional_vscode: {"ParentImage|endswith": "\\AppData\\Local\\Programs\\Microsoft VS Code\\Code.exe"}
filter_optional_pip: {"CommandLine|contains|all": ["<pip-setuptools-caller>", "exec(compile("]}
