detection.query:
selection_reg_img: [{"Image|endswith": "\\reg.exe"}, {"OriginalFileName": "reg.exe"}]
selection_reg_flag: {"CommandLine|contains": "add"}
selection_keys: {"CommandLine|contains": ["Control Panel\\Desktop", "CurrentVersion\\Policies\\ActiveDesktop", "CurrentVersion\\Policies\\System"]}
selection_cli_reg_1: {"CommandLine|contains|all": ["/v NoChangingWallpaper", "/d 1"]}
selection_cli_reg_2: {"CommandLine|contains|all": ["/v Wallpaper", "/t REG_SZ"]}
selection_cli_reg_3: {"CommandLine|contains|all": ["/v WallpaperStyle", "/d 2"]}
