detection.query:
selection: {"QueryName|startswith": "_ldap."}
filter_main_generic: {"Image|contains": [":\\Program Files\\", ":\\Program Files (x86)\\", ":\\Windows\\"]}
filter_main_defender: {"Image|contains": ":\\ProgramData\\Microsoft\\Windows Defender\\Platform\\", "Image|endswith": "\\MsMpEng.exe"}
filter_main_unknown: {"Image": "<unknown process>"}
filter_optional_azure: {"Image|startswith": "C:\\WindowsAzure\\GuestAgent"}
filter_main_null: {"Image": null}
filter_optional_browsers: {"Image|endswith": ["\\chrome.exe", "\\firefox.exe", "\\opera.exe"]}
