detection.query:
selection_pwsh_binary: [{"Image|endswith": ["\\powershell.exe", "\\pwsh.exe"]}, {"OriginalFileName": ["PowerShell.EXE", "pwsh.dll"]}]
selection_pwsh_cli: {"CommandLine|contains": ["-DisableBehaviorMonitoring $true", "-DisableRuntimeMonitoring $true"]}
selection_sc_binary: [{"Image|endswith": "\\sc.exe"}, {"OriginalFileName": "sc.exe"}]
selection_sc_tamper_cmd_stop: {"CommandLine|contains|all": ["stop", "WinDefend"]}
selection_sc_tamper_cmd_delete: {"CommandLine|contains|all": ["delete", "WinDefend"]}
selection_sc_tamper_cmd_disabled: {"CommandLine|contains|all": ["config", "WinDefend", "start=disabled"]}
