detection.query:
selection_wevtutil_img: [{"Image|endswith": "\\wevtutil.exe"}, {"OriginalFileName": "wevtutil.exe"}]
selection_wevtutil_cmd: {"CommandLine|contains": ["clear-log ", " cl ", "set-log ", " sl ", "lfn:"]}
selection_other_ps_img: {"Image|endswith": ["\\powershell.exe", "\\powershell_ise.exe", "\\pwsh.exe"]}
selection_other_ps_cmd: [{"CommandLine|contains": ["Clear-EventLog ", "Remove-EventLog ", "Limit-EventLog ", "Clear-WinEvent "]}, {"CommandLine|contains|all": ["Eventing.Reader.EventLogSession", "ClearLog"]}, {"CommandLine|contains|all": ["Diagnostics.EventLog", "Clear"]}]
selection_other_wmi: {"Image|endswith": ["\\powershell.exe", "\\powershell_ise.exe", "\\pwsh.exe", "\\wmic.exe"], "CommandLine|contains": "ClearEventLog"}
filter_main_msiexec: {"ParentImage": ["C:\\Windows\\SysWOW64\\msiexec.exe", "C:\\Windows\\System32\\msiexec.exe"], "CommandLine|contains": " sl "}
