detection.query:
selection_user: {"User|contains": ["AUTHORI", "AUTORI"]}
selection_image_1: {"Image|contains|all": ["\\Windows\\Installer\\", "msi"], "Image|endswith": "tmp"}
selection_image_2: {"Image|endswith": "\\msiexec.exe", "IntegrityLevel": ["System", "S-1-16-16384"]}
filter_installer: {"ParentImage": "C:\\Windows\\System32\\services.exe"}
filter_repair: [{"CommandLine|endswith": "\\system32\\msiexec.exe /V"}, {"ParentCommandLine|endswith": "\\system32\\msiexec.exe /V"}]
filter_sophos: {"ParentImage|startswith": "C:\\ProgramData\\Sophos\\"}
filter_avira: {"ParentImage|startswith": "C:\\ProgramData\\Avira\\"}
filter_avast: {"ParentImage|startswith": ["C:\\Program Files\\Avast Software\\", "C:\\Program Files (x86)\\Avast Software\\"]}
filter_google_update: {"ParentImage|startswith": ["C:\\Program Files\\Google\\Update\\", "C:\\Program Files (x86)\\Google\\Update\\"]}
