detection.query:
selection_schtasks: {"Image|endswith": "\\schtasks.exe", "CommandLine|contains": "/Create "}
selection_pattern_1: {"CommandLine|contains": ["/sc minute ", "/ru system "]}
selection_pattern_2: {"CommandLine|contains": ["cmd /c", "cmd /k", "cmd /r", "cmd.exe /c ", "cmd.exe /k ", "cmd.exe /r "]}
selection_uncommon: {"CommandLine|contains": [" -decode ", " -enc ", " -w hidden ", " bypass ", " IEX", ".DownloadData", ".DownloadFile", ".DownloadString", "/c start /min ", "FromBase64String", "mshta http", "mshta.exe http"]}
selection_anomaly_1: {"CommandLine|contains": [":\\ProgramData\\", ":\\Temp\\", ":\\Tmp\\", ":\\Users\\Public\\", ":\\Windows\\Temp\\", "\\AppData\\", "%AppData%", "%Temp%", "%tmp%"]}
selection_anomaly_2: {"CommandLine|contains": ["cscript", "curl", "wscript"]}
