detection.query:
selection_tools_cmd: {"Image|endswith": "\\cmd.exe", "CommandLine|contains": "copy "}
selection_tools_pwsh: {"Image|endswith": ["\\powershell.exe", "\\pwsh.exe"], "CommandLine|contains": ["copy-item", " copy ", "cpi ", " cp "]}
selection_tools_other: [{"Image|endswith": ["\\robocopy.exe", "\\xcopy.exe"]}, {"OriginalFileName": ["robocopy.exe", "XCOPY.EXE"]}]
selection_target_path: {"CommandLine|contains": ["\\System32", "\\SysWOW64", "\\WinSxS"]}
selection_target_lolbin: {"CommandLine|contains": ["\\bitsadmin.exe", "\\calc.exe", "\\certutil.exe", "\\cmdl32.exe", "\\cscript.exe", "\\mshta.exe", "\\rundll32.exe", "\\wscript.exe"]}
